// Learning from human decisions
const humanDecision = $input.all()[0].json;

if (humanDecision.action === 'override') {
  // Update AI models with human feedback
  const feedback = {
    originalDecision: humanDecision.aiRecommendation,
    humanOverride: humanDecision.overrideDecision,
    reason: humanDecision.overrideReason,
    context: humanDecision.context,
    timestamp: new Date().toISOString()
  };

  // This helps AI learn from human expertise
  await $http.post($vars.AI_AGENTS_API + '/learn/feedback', {
    body: feedback
  });
}

// Process the final decision
return [{
  ...humanDecision,
  finalDecision: humanDecision.action === 'approve' 
    ? humanDecision.aiRecommendation 
    : humanDecision.overrideDecision,
  processedBy: 'human_review',
  learningCaptured: true
}];

